import React, { useState } from "react";
import "./AddUser.css";
import AddUserButton from "./AddUserButton";

const AddUser = (props) => {
  const [enteredUser, setEnteredUser] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const inputUserHandler = (event) => {
    setEnteredUser(event.target.value);
  };
  const inputAgeHandler = (event) => {
    setEnteredAge(event.target.value);
  };
  const addUserHandler = (event) => {
    event.preventDefault();
    const listItemes = {
      userName: enteredUser,
      age: enteredAge,
    };
    enteredUser.trim.length > 0 && enteredAge.trim.length > 0
      ? props.onAddUser(listItemes)
      : alert("empty");
    setEnteredUser("");
    setEnteredAge("");
  };

  return (
    <form onSubmit={addUserHandler}>
      <div className="form-controler">
        <div className="form-controler__items">
          <lable>UserName</lable>
          <input type="text" onChange={inputUserHandler}></input>
          <lable>Age(years)</lable>
          <input type="number" onChange={inputAgeHandler}></input>
          <AddUserButton />
        </div>
      </div>
    </form>
  );
};

export default AddUser;
