import react, { useState } from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

const NewExpense = (props) => {
  const saveDataHandler = (enterExpenseData) => {
    const expenseData = {
      ...enterExpenseData,
      id: 1,
    };
    props.onAddNewExpens(expenseData);
  };
  const [clicked, setClicked] = useState("");
  const newButtonHandler = (event) => {
    setClicked(event.preventDefault());
  };
  return (
    <div className="new-expense">
      {setClicked ? (
        <ExpenseForm onSaveExpenseData={saveDataHandler} />
      ) : (
        <button onClick={newButtonHandler}>Add New Expense</button>
      )}
    </div>
  );
};
export default NewExpense;
